    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String informacion = "Su DNI es " + jTextField1.getText();
        
        informacion+= "\n Su nombre es " + jTextField2.getText();
        
        informacion+= "\n Su apellido es " + jTextField3.getText();
        
        informacion+= "\n La fecha de nacimiento es " + jComboBox1.getSelectedItem() + " de " + jComboBox2.getSelectedItem() + "del a√±o " + jComboBox3.getSelectedItem();
        
        JOptionPane.showMessageDialog(this, informacion,"Muestra de datos", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton1ActionPerformed

